	var simpleAlert = function (opts) {
    //璁剧疆榛樿鍙傛暟
    var opt = {
        "closeAll": false,
        "imgshow":0,
        "content": "",
        "tit": "",
        "list1_tit": "姓名",
        "list2_tit": "联系方式",
        "list3_tit": "买家认证",
        "list4_tit": "地址",
        "list5_tit": "",
        "list1_txt": "",
        "list2_txt": "",
        "list3_txt": "",
        "list4_txt": "",
        "list5_txt": "",
        "buttons": {}
    }
    //鍚堝苟鍙傛暟
    var option = $.extend(opt, opts);
    //浜嬩欢
    var dialog = {}
    var $simpleAlert = $('<div class="simpleAlert">');
    var $shelter = $('<div class="simpleAlertShelter">');
    var $simpleAlertBody = $('<div class="simpleAlertBody">');
    var $simpleAlertBodyClose = $('<b class="simpleAlertBodyClose">x</b>');
    var $simpleAlertBodyContent = $('<div class="simpleAlertBodyContent">' 
                                        + '<i class="left_btn">  </i>'+  
                                            '<div class="BodyContent">'+  
                                                '<div class="BodyDiv">'+
                                                   option.content +  option.content1 +
                                                   '</div>' 
                                            + '</div>'
                                        + '<i class="right_btn">  </i>'
                                    + '</div>');
   var $simpleAlertDiv=$('<div class="alert_mess">' +
        '<h6 class="alert_mess">' + option.tit +'</h6>' +
        '<ul class="alert_mess_list">'+
           ' <li class="alert_group"> '+
                '<dl>'+
                    '<dt><i></i>' + option.list1_tit + '：</dt>'+
                    '<dd>'+ option.list1_txt + '</dd>'+
                '</dl>'+
            '</li>'+
           ' <li class="alert_group">'+
                '<dl>'+
                    '<dt><i></i> '+ option.list2_tit + '：</dt>'+
                   ' <dd> ' + option.list2_txt + '</dd>' +
                '</dl>'+
            '</li>' +
           ' <li class="alert_group">'+
               ' <dl> '+
                   '<dt><i></i>' +  option.list3_tit +'：</dt>'+
                    ' <dd class="red">' + option.list3_txt + '</dd> '+
                '</dl>'+
           ' </li>'+
           ' <li class="alert_group"> '+
               '<dl>'+
                    '<dt><i></i> ' + option.list4_tit + '：</dt>'+
                    '<dd>' + option.list4_txt + ' </dd>'+
                '</dl>' +
           ' </li>'+

        ' </ul>'+
   ' </div>')


    dialog.init = function () {
        if(option.imgshow>0){
            $simpleAlertBody.append($simpleAlertBodyClose).append($simpleAlertDiv);
        }else{
              $simpleAlertBody.append($simpleAlertBodyClose).append($simpleAlertBodyContent);
        }
      
      
        
        var num = 0;
        var only = false;
        var onlyArr = [];
        for (var i = 0; i < 2; i++) {
            for (var key in option.buttons) {
                switch (i) {
                    case 0:
                        onlyArr.push(key);
                        break;
                    case 1:
                        if (onlyArr.length <= 1) {
                            only = true;
                        } else {
                            only = false;
                        }
                        num++;
                        var $btn = $('<button class="simpleAlertBtn simpleAlertBtn' + num + '">' + key + '</button>')
                        $btn.bind("click", option.buttons[key]);
                        if (only) {
                            $btn.addClass("onlyOne")
                        }
                        $simpleAlertBody.append($btn);
                        break;
                }

            }
        }
        $simpleAlert.append($shelter).append($simpleAlertBody);
        $("body").append($simpleAlert);
         var alertx=$('.BodyDiv img').length;
        
         $('.simpleAlertBodyContent  .BodyDiv').css('width',alertx*100+"%")
         var alertdivx=$('.simpleAlertBodyContent  .BodyDiv').width() + "%";
         $('.BodyDiv img').css('width',alertdivx/alertx )
         

        $simpleAlertBody.show().animate({"marginTop":"-128px","opacity":"1"},300);
    }
    //鍙充笂瑙掑叧闂寜閿簨浠�
    $simpleAlertBodyClose.bind("click", function () {
        option.closeAll=false;
        dialog.close();
    })



    dialog.close = function () {
        if(option.closeAll){
            $(".simpleAlert").remove()
        }else {
            $simpleAlertBody.animate({"marginTop": "-188px", "opacity": "0"}, 200, function () {
                $(".simpleAlert").last().remove()
            });
        }
    }
    dialog.init();
    return dialog;
}